
public class StarPettern5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,k;
		for(i=0;i<=5;i++)
	      {
	    	  for(j=1;j<=i;j++)
	    	  {
	    		  System.out.print(" ");
	    	  }
	    	  
	      
		for(k=4;k>=i;k--)
		{
			System.out.print("*");
		}
		System.out.println();
	}


	}

}
