//Write a Java program to print the ASCII value of a given character.
public class AsciiValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1='A';
		char ch2='B';
		char ch3='9';
		
		int As1=ch1;
		int As2=ch2;
		int As3=ch3;
		System.out.println("The ASCII VALU:" +ch1+ "IS:" +As1);
		System.out.println("The ASCII VALUE:" +ch2+ "IS:" +As2);
		System.out.println("The ASCII VALUE:" +ch3+ "IS:" +As3);
	}

}
